package com.duapps.affair.demo.bean;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;@Data@NoArgsConstructor@AllArgsConstructorpublic class PageRequest implements Serializable {    private int pageSize;    private int currentPage;    public int getPageSize() {        if (pageSize <= 0) {            pageSize = 1;        }        return pageSize;    }    public int getCurrentPage() {        if (currentPage <= 0) {            currentPage = 1;        }        return currentPage;    }    public int getLimit() {        return getPageSize();    }    public int getOffset() {        return (getCurrentPage() - 1) * getPageSize();    }}