package com.duapps.affair.demo.bean;import lombok.Data;import java.io.Serializable;import java.util.Collections;import java.util.List;/** * @Author he.zhou * @Date 2020-08-31 */@Datapublic class Pager<T> implements Serializable {    private int curPage;    private int pageSize;    private long totalSize;    private List<T> data;    public static <T> Pager<T> of(Long totalSize, Integer pageSize, Integer curPage, List<T> data) {        Pager<T> pager = new Pager<>();        pager.curPage = curPage;        pager.pageSize = pageSize;        pager.totalSize = totalSize;        pager.data = data == null ? Collections.emptyList() : data;        return pager;    }}