package com.duapps.affair.demo.common;import org.springframework.cglib.proxy.Enhancer;import org.springframework.cglib.proxy.MethodInterceptor;/** * @Author he.zhou * @Date 2020-12-19 */public class MetaspaceOOMTest {    public static class OOMTest {    }    public static void main(String[] args) {        Enhancer enhancer = new Enhancer();        enhancer.setSuperclass(OOMTest.class);        enhancer.setUseCache(false);        enhancer.setCallback((MethodInterceptor) (o, method, objects, methodProxy) -> methodProxy.invoke(o, args));        enhancer.create();    }}