package com.duapps.affair.demo.elasticsearch.model;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.elasticsearch.common.geo.GeoPoint;import org.springframework.data.annotation.Id;import org.springframework.data.elasticsearch.annotations.Document;import org.springframework.data.elasticsearch.annotations.GeoPointField;/** * @Author he.zhou * @Date 2020-08-26 */@Data@Document(indexName = "user_location")@AllArgsConstructor@NoArgsConstructorpublic class UserLocation {    @Id    private Long id;    private Long userId;    @GeoPointField    private GeoPoint location;}