package com.duapps.affair.demo.elasticsearch.request;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.data.domain.Sort;/** * @Author he.zhou * @Date 2020-08-25 */@Data@NoArgsConstructor@AllArgsConstructorpublic class EsBaseReq {    private int pageSize;    private int currentPage;    private String sortFiled;    private Sort.Direction direction;    public int getPageSize() {        if (pageSize <= 0) {            pageSize = 1;        }        return pageSize;    }    public int getCurrentPage() {        if (currentPage <= 0) {            currentPage = 1;        }        return currentPage;    }    public int getLimit() {        return getPageSize();    }    public int getOffset() {        return (getCurrentPage() - 1) * getPageSize();    }}