package com.duapps.affair.demo.elasticsearch.request;import com.duapps.affair.demo.elasticsearch.model.StoreBaseInfo;import com.duapps.affair.demo.elasticsearch.model.StoreTags;import com.duapps.affair.demo.elasticsearch.util.EsEquals;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.springframework.data.elasticsearch.annotations.Document;import java.util.List;/** * @Author he.zhou * @Date 2020-08-25 */@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Document(indexName = "store")public class StoreReq extends EsBaseReq {    @EsEquals(name = "id")    private Long id;    @EsEquals    private String code;    @EsEquals(name = "baseInfo.address")    private String baseInfoAddress;    /**     * 标签     */    private List<StoreTags> tags;}