package com.duapps.affair.demo.elasticsearch.service;/** * @Author he.zhou * @Date 2020-08-25 */public interface EsIndexService {    /**     * 创建索引     *     * @param t     * @return     */    <T> boolean createIndex(Class<T> t);    /**     * 删除索引     *     * @param t     * @return     */    <T> boolean deleteIndex(Class<T> t);    /**     * 重建索引，数据会被删除     *     * @param t     * @return     */    <T> boolean reCreate(Class<T> t);    /**     * 判断索引是否存在     *     * @param t     * @return     */    <T> boolean indexExists(Class<T> t);}