package com.duapps.affair.demo.redLock;import org.apache.commons.lang3.StringUtils;import org.redisson.Redisson;import org.redisson.api.RedissonClient;import org.redisson.config.ClusterServersConfig;import org.redisson.config.Config;import org.redisson.config.SingleServerConfig;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;/** * @Author he.zhou * @Date 2021-04-12 */@Componentpublic class RedissonConnector {    RedissonClient redisson;    @PostConstruct    public void init() {        Config config = new Config();        config.useClusterServers()                .addNodeAddress("redis://10.29.10.14:8700");        redisson = Redisson.create(config);    }    public RedissonClient getClient() {        return redisson;    }}